mapper.update:
update tbl_message
set readstate = 'y'
where mno = #{mno}

mapper.select:
select mes.mno, mes.sender, mes.receiver, mes.content, mes.readstate, mes.senddate, mes.readdate, mem.username receivername
from tbl_message mes ,tbl_member mem
where mem.userid = mes.receiver AND mno = #{mno}

mapper.update:
update tbl_message
set readstate = 'c'
where mno = #{mno}

update tbl_message
set senderstate = 'd'
where mno = #{mno}

update tbl_message
set receiverstate = 'd'
where mno = #{mno}
