mapper.update:
update tbl_basket
set order_count = #{order_count}
where shoes_code = #{shoes_code}

mapper.select:
select order_count 
from tbl_basket
where shoes_code = #{shoes_code}

select count(shoes_code)  
from tbl_basket
where user_id = #{user_id}

select count(*) 
from tbl_basket
where shoes_code = #{shoes_code}

mapper.update:
update tbl_basket
set order_count = order_count + 1
where shoes_code = #{shoes_code}
