mapper.select:
select next,prev 
	from (
	    select bno, 
	    lead(bno,1) over(order by bno) as next, 
	    lag(bno,1) over(order by bno) as prev from tbl_board
	)
where bno = #{bno}

mapper.update:
update tbl_board set
	title = #{title},
	content = #{content}
where bno = #{bno}

update tbl_board set
	viewcnt = viewcnt + 1
where bno = #{bno}
